mapper.select:
select *
from model_basic
where 1=1

    and model_basic.name like #{map.name}


    and model_basic.module like #{map.module}

select b.*,_v.install_time,revision from model_basic b left join (select v.model_basic_uuid,max(v.install_time) as install_time,v.revision from model_version v GROUP BY v.model_basic_uuid) _v on _v.model_basic_uuid = b.model_uuid where 1 = 1
and b.name like #{map.name}
and b.module like #{map.module}
